// Tento node vytvorí normalizované ISO polia s offsetom a +30 min end,
// a vyextrahuje meno a rok narodenia pre ďalšie nody.

const tz = "Europe/Bratislava";       // n8n node môže mať TZ v settings; tu jasne určujeme
const DURATION_MIN = 30;

// Pomocná: získa minútový offset voči UTC pre daný Date v danej TZ
function getOffsetMinutes(date, timeZone) {
  const fmt = new Intl.DateTimeFormat('en-US', {
    timeZone,
    timeZoneName: 'shortOffset',
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false,
  });
  const parts = fmt.formatToParts(date);
  const tzName = parts.find(p => p.type === 'timeZoneName')?.value || '';
  const m = tzName.match(/([+-]\d+)(?::(\d+))?/);
  if (!m) return 0;
  const hours = Number(m[1]);
  const minutes = Number(m[2] || 0);
  return hours  60 + minutes;
}

// Pomocná: vytvorí "zónovaný" Date (lokálny čas v danej TZ prevedie na UTC Date objekt)
function makeZonedDate(y, m, d, hh, mm, timeZone) {
  const utcMs = Date.UTC(y, m - 1, d, hh, mm, 0, 0);
  const temp = new Date(utcMs);
  const offsetMin = getOffsetMinutes(temp, timeZone);
  return new Date(utcMs - offsetMin  60000);
}

// Pomocná: ISO string s offsetom, napr. 2025-11-12T10:00:00+01:00
function toIsoWithOffset(date, timeZone) {
  const fmt = new Intl.DateTimeFormat('en-GB', {
    timeZone,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false,
  });
  const parts = fmt.formatToParts(date);
  const y = parts.find(p => p.type === 'year').value;
  const m = parts.find(p => p.type === 'month').value;
  const d = parts.find(p => p.type === 'day').value;
  const hh = parts.find(p => p.type === 'hour').value;
  const mm = parts.find(p => p.type === 'minute').value;
  const ss = parts.find(p => p.type === 'second').value;

  const offMin = getOffsetMinutes(date, timeZone);
  const sign = offMin >= 0 ? '+' : '-';
  const abs = Math.abs(offMin);
  const oh = String(Math.floor(abs / 60)).padStart(2, '0');
  const om = String(abs % 60).padStart(2, '0');

  return `${y}-${m}-${d}T${hh}:${mm}:${ss}${sign}${oh}:${om}`;
}

// Parsovanie "dd.mm.yyyy HH:MM"
function parseSkDateTime(s) {
  const m = s.match(/^(\d{1,2})\.(\d{1,2})\.(\d{4})\s+(\d{1,2}):(\d{2})$/);
  if (!m) throw new Error(`Invalid datetime format: "${s}". Expected "dd.mm.yyyy HH:MM"`);
  const d = Number(m[1]);
  const mon = Number(m[2]);
  const y = Number(m[3]);
  const hh = Number(m[4]);
  const mm = Number(m[5]);
  if (hh < 0 || hh > 23 || mm < 0 || mm > 59) throw new Error("Invalid time range");
  return { y, mon, d, hh, mm };
}

